mapper.select:
SELECT cno, bno, IFNULL(pcno, cno) AS pcno, comment, commenter, reg_date, up_date
FROM comment_2
WHERE bno = #{bno}
ORDER BY pcno ASC, cno ASC;

SELECT cno, bno, pcno, comment, commenter, reg_date, up_date
FROM comment_2
WHERE cno = #{cno}

mapper.update:
UPDATE comment_2
SET comment = #{comment}
  , up_date = now()
WHERE cno = #{cno} and commenter = #{commenter}
